import requestsimport jsonimport osimport requests.exceptionsimport sysfrom os import listdirfrom os.path import isfile, joinimport codecsDCM_URL='https://apis.lynx-project.eu/document-platforms/upm-elastic'def setDCM_URL(url):    DCM_URL=urldef getAllDocumentsFromIndex(index, max_docs=10000):  offset=0  limit= 200  Documents=[]     cont= True  loop=0    while cont:    docs= getDocumentsFromIndex(index, limit, offset)    print('loop: '+str(loop))    loop=loop+1    if len(docs)==0:      cont=False        Documents.extend(docs)    offset=offset+limit    if len(Documents)>=max_docs:        cont=False        break  #print(jsonResponse)  #print(response.status_code)  return Documents            def getDocumentsFromIndex(index,limit=10, offset=0):  Documents=[]    hed = {           'accept': 'application/json'           ,'Content-Type': 'application/json;charset=UTF-8'                      }    url=DCM_URL+'/collections/'+index+'/documents?sort=asc&limit='+str(limit)+'&offset='+str(offset)  response = requests.get(url, headers=hed)  jsonResponse = response.json()  docs= jsonResponse.get('docs')      Documents.extend(docs)  #print(jsonResponse)  #print(response.status_code)  return Documents  def dcmPostDocument(index, data):        hed = {           'accept': 'application/json'           ,'Content-Type': 'application/json;charset=UTF-8'                      }        url=DCM_URL+'/collections/'+index+'/documents'            response = requests.post(url, json=data, headers=hed)    return response            def dcmPutDocument(index, data):        hed = {           'accept': 'application/json'           ,'Content-Type': 'application/json;charset=UTF-8'                      }        url=DCM_URL+'/collections/'+index+'/documents'            response = requests.put(url, json=data, headers=hed)    return response            def writeJsonFiles(ide,data):            file = codecs.open(ide, "w", "utf-8")    file.write(data)    file.close()def readJsonFiles(path):        jsons=[]    for entry in os.listdir(path):        if os.path.isfile(os.path.join(path, entry)):                        jsons.append(readJsonFile(os.path.join(path, entry)))        return jsons        def readJsonFile(file):        with open(file,encoding="utf8") as json_file:            data = json.load(json_file)                        return data                    return def filterFiles(jsondocs):        lis= []    for j in jsondocs:        data= filterFile(j)        lis.append(data)                return lisdef filterFile(data):        data['@id']=data['@id'].split('documents/')[1]    if 'parts' not in data:             return data      parts= data['parts']    for p in parts:                 title=''        try:             title = p['title']            del p['title']        except:            pass        newtitle={}        newtitle['de']= title        p['title']= newtitle        #p['title']={"de":title}           return data'''collections part'''def getCollections():    url=DCM_URL+'/collections/'    response = requests.get(url)    return response.json()def postCollections(name):        url=DCM_URL+'/collections/'+str(name)    response = requests.post(url)    return response     def deleteCollections(name):         url=DCM_URL+'/collections/'+str(name)    response = requests.delete(url)    return response.json()'''getCollections()deleteCollections('collection_review')postCollections('collection_review')'''docExample={  "@context": "http://lynx-project.eu/doc/jsonld/lynxdocument.json",  "@id": "lis/collections/lawthek/documents/6916ac50-6e70-4e53-b489-6f74b7814847",  "@type": [    "lkg:LynxDocument",    "nif:Context",    "lkg:Legislation"  ],  "metadata": {    "type_document": "Law",    "version": "Consolidated",    "language": "de",    "jurisdiction": "AT",    "title": {      "de": "Soziale Sicherheit (Serbien)"    },    "hasAuthority": "BGBl. III Nr. 155/2012",    "id_local": "20008057",    "first_date_entry_in_force": "2012-12-01",    "version_date": "2012-12-01",    "url_consolidated": "http://www.ris.bka.gv.at/GeltendeFassung.wxe?Abfrage\u003dBundesnormen\u0026Gesetzesnummer\u003d20008057\u0026FassungVom\u003d2012-12-01",    "classifications": [      "69/03 Soziale Sicherheit"    ],    "seeAlso": [      {        "@id": "https://lawthek.eu/detail/6916ac50-6e70-4e53-b489-6f74b7814847/de/MULTI",        "format": "text/html"      },      {        "@id": "https://api.lawthek.eu/api/v2/legalObjects/6916ac50-6e70-4e53-b489-6f74b7814847/de",        "format": "application/json"      }    ]  },  "offset_ini": 0,  "offset_end": 40225,  "text": "Sonstige Textteile Der Nationalrat hat beschlossen: Der Abschluss des gegenständlichen Staatsvertrages wird gemäß Art. 50 Abs. 1 Z 1 B-VG genehmigt. Ratifikationstext Die vom Bundespräsidenten unterzeichnete und vom Bundeskanzler gegengezeichnete Ratifikationsurkunde wurde am 25. Oktober 2012 ausgetauscht; das Abkommen tritt daher gemäß seinem Art. 38 Abs. 2 mit 1. Dezember 2012 in Kraft. Präambel/Promulgationsklausel Die Republik Österreich und die Republik Serbien, von dem Wunsche geleitet, die gegenseitigen Beziehungen zwischen den beiden Staaten auf dem Gebiet der sozialen Sicherheit zu regeln, sind übereingekommen, folgendes Abkommen zu schließen:ABSCHNITT I ALLGEMEINE BESTIMMUNGEN Artikel 1 Begriffsbestimmungen (1) In diesem Abkommen bedeuten die Ausdrücke 1. „Rechtsvorschriften“ die Gesetze, Verordnungen, Satzungen und sonstige allgemein rechtsetzende Akte, die sich auf die in Artikel 2 Absatz 1 des Abkommens bezeichneten Zweige der sozialen Sicherheit beziehen; 2. „zuständige Behörde“ in Bezug auf die Republik Österreich die Bundesminister, die mit der Anwendung der in Artikel 2 Absatz 1 Ziffer 1 des Abkommens angeführten Rechtsvorschriften betraut sind, in Bezug auf die Republik Serbien die Ministerien, die mit der Anwendung der in Artikel 2 Absatz 1 Ziffer 2 des Abkommens angeführten Rechtsvorschriften betraut sind; 3. „Träger“ die Einrichtung oder Behörde, der die Durchführung der in Artikel 2 Absatz 1 des Abkommens bezeichneten Rechtsvorschriften obliegt; 4. „zuständiger Träger“ den Träger, bei dem eine Person im Zeitpunkt des Antrages auf Leistung versichert ist oder gegen den sie einen Anspruch auf Leistungen hat oder noch hätte, wenn sie sich im Gebiet des Vertragsstaates, in dem sie zuletzt versichert war, aufhalten würde; 5. „Wohnort“ den Ort des gewöhnlichen Aufenthaltes; 6. „Aufenthalt“ den vorübergehenden Aufenthalt; 7. „Familienangehöriger“ einen Familienangehörigen nach den Rechtsvorschriften des Vertragsstaates, in dem der Träger, zu dessen Lasten die Leistungen zu gewähren sind, seinen Sitz hat; 8. „Versicherungszeiten“ Beitragszeiten und gleichgestellte Zeiten, die nach den Rechtsvorschriften der beiden Vertragsstaaten als solche gelten; 9. „Geldleistung“, „Rente“ oder „Pension“ eine Geldleistung, Rente oder Pension einschließlich aller ihrer Teile aus öffentlichen Mitteln, aller Zuschläge, Anpassungsbeträge und Zulagen sowie Kapitalabfindungen und Zahlungen, die als Beitragserstattungen geleistet werden. (2) In diesem Abkommen haben andere Ausdrücke die Bedeutung, die ihnen nach den Rechtsvorschriften der beiden Vertragsstaaten zukommt.Artikel 2 Sachlicher Geltungsbereich (1) Dieses Abkommen bezieht sich 1. auf die österreichischen Rechtsvorschriften über a) die Krankenversicherung, b) die Unfallversicherung, c) die Pensionsversicherung mit Ausnahme der Sonderversicherung für das Notariat, d) das Arbeitslosengeld; 2. auf die serbischen Rechtsvorschriften über a) die Krankenversicherung, den Gesundheitsschutz und die Mutterschaft, b) die Pensions- und Invalidenversicherung, c) das Arbeitslosengeld. (2) Dieses Abkommen bezieht sich auch auf alle Rechtsvorschriften, welche die in Absatz 1 dieses Artikels bezeichneten Rechtsvorschriften zusammenfassen, ändern oder ergänzen.Artikel 3 Persönlicher Geltungsbereich Dieses Abkommen gilt a) für Personen, für die die Rechtsvorschriften eines oder beider Vertragsstaaten gelten oder galten; b) für andere Personen, soweit diese ihre Rechte von den im Buchstaben a bezeichneten Personen ableiten.Artikel 4 Gleichbehandlung (1) Bei Anwendung der Rechtsvorschriften eines Vertragsstaates stehen, soweit dieses Abkommen nichts anderes bestimmt, dessen Staatsangehörigen die Staatsangehörigen des anderen Vertragsstaates gleich. (2) Absatz 1 dieses Artikels berührt nicht a) die Rechtsvorschriften betreffend die Mitwirkung der Versicherten und der Dienstgeber in den Organen der Träger und der Verbände sowie in der Rechtsprechung im Bereich der sozialen Sicherheit; b) Versicherungslastregelungen in zwischenstaatlichen Verträgen der Vertragsstaaten mit anderen Staaten; c) die Rechtsvorschriften betreffend die Versicherung der bei einer amtlichen Vertretung eines der beiden Vertragsstaaten in Drittstaaten oder bei Mitgliedern einer solchen Vertretung beschäftigten Personen. (3) Absatz 1 dieses Artikels gilt hinsichtlich der österreichischen Rechtsvorschriften über die Berücksichtigung von Kriegsdienstzeiten oder diesen gleichgestellten Zeiten nur für serbische Staatsangehörige, die unmittelbar vor dem 13. März 1938 die österreichische Staatsangehörigkeit besaßen.Artikel 5 Leistungstransfer (1) Pensionen, Renten und andere Geldleistungen mit Ausnahme der Leistungen bei Arbeitslosigkeit, auf die nach den Rechtsvorschriften eines Vertragsstaates Anspruch besteht, dürfen nicht deshalb gekürzt, geändert, zum Ruhen gebracht, entzogen oder beschlagnahmt werden, weil der Berechtigte im Gebiet des anderen Vertragsstaates wohnt. (2) Absatz 1 dieses Artikels bezieht sich nicht a) auf die Ausgleichszulage beziehungsweise Einmalzahlungen als Kaufkraftausgleich nach den österreichischen Rechtsvorschriften; b) auf die Aufstockung der Pension auf den Mindestbetrag nach den serbischen Rechtsvorschriften.ABSCHNITT II BESTIMMUNGEN ÜBER DIE ANZUWENDENDEN RECHTSVORSCHRIFTEN Artikel 6 Allgemeine Regelung (1) Die Versicherungspflicht einer Person richtet sich, soweit die Artikel 7 und 8 des Abkommens nichts anderes bestimmen, nach den Rechtsvorschriften des Vertragsstaates, in dessen Gebiet die Erwerbstätigkeit ausgeübt wird, und zwar auch dann, wenn sich der Wohnort des Dienstnehmers oder der Sitz des Dienstgebers im Gebiet des anderen Vertragsstaates befindet. (2) Für Beamte und ihnen gleichgestellte Personen gelten die Rechtsvorschriften des Vertragsstaates, bei dessen Verwaltung sie beschäftigt sind.Artikel 7 Besondere Regelungen (1) Wird ein Dienstnehmer von einem Unternehmen mit Sitz im Gebiet eines der Vertragsstaaten in das Gebiet des anderen Vertragsstaates entsendet, so sind bis zum Ende des 24. Kalendermonates nach dieser Entsendung die Rechtsvorschriften des ersten Vertragsstaates weiter anzuwenden, als wäre er noch in dessen Gebiet beschäftigt. (2) Wird ein Dienstnehmer eines Luftfahrtunternehmens mit dem Sitz im Gebiet eines Vertragsstaates aus dessen Gebiet in das Gebiet des anderen Vertragsstaates entsendet, so sind die Rechtsvorschriften des ersten Vertragsstaates weiter anzuwenden, als wäre er noch in dessen Gebiet beschäftigt. (3) Die Besatzung eines Seeschiffes sowie andere nicht nur vorübergehend auf einem Seeschiff beschäftigte Personen unterliegen den Rechtsvorschriften des Vertragsstaates, dessen Flagge das Schiff führt.Artikel 8 Diplomatisches und konsularisches Hilfspersonal (1) Für Mitglieder der diplomatischen Vertretungen oder konsularischen Dienststellen, die nicht Beamte oder ihnen gleichgestellte Personen sind, und private Hausangestellte im Dienst von Mitgliedern dieser Vertretungen oder Dienststellen, die in das Gebiet des anderen Vertragsstaates entsendet werden, gelten die Rechtsvorschriften des Vertragsstaates, aus dem sie entsendet werden. (2) Für die in Absatz 1 dieses Artikels bezeichneten Dienstnehmer, die nicht entsendet sind, gelten die Rechtsvorschriften des Vertragsstaates, in dessen Gebiet sie beschäftigt sind.Artikel 9 Ausnahmen (1) Auf gemeinsamen Antrag des Dienstnehmers und seines Dienstgebers können die zuständigen Behörden der beiden Vertragsstaaten einvernehmlich Ausnahmen von den Artikeln 6 bis 8 des Abkommens vereinbaren, wobei auf die Art und die Umstände der Beschäftigung Bedacht zu nehmen ist. (2) Gelten für einen Dienstnehmer nach Absatz 1 dieses Artikels die Rechtsvorschriften des einen Vertragsstaates, obwohl er die Beschäftigung im Gebiet des anderen Vertragsstaates ausübt, so sind die Rechtsvorschriften so anzuwenden, als ob er diese Beschäftigung im Gebiet des ersten Vertragsstaates ausüben würde.ABSCHNITT III BESONDERE BESTIMMUNGEN Kapitel 1 Krankheit und Mutterschaft Artikel 10 Zusammenrechnung der Versicherungszeiten Hat eine Person nach den Rechtsvorschriften beider Vertragsstaaten Versicherungszeiten erworben, so sind diese, soweit erforderlich, für den Erwerb eines Leistungsanspruches und die Dauer der Leistungsgewährung zusammenzurechnen, soweit sie nicht auf dieselbe Zeit entfallen.Artikel 11 Sachleistungen (1) Eine Person, welche die nach den Rechtsvorschriften eines Vertragsstaates für den Leistungsanspruch erforderlichen Voraussetzungen erfüllt und a) die im Gebiet des anderen Vertragsstaates wohnt oder b) deren Zustand während eines Aufenthaltes im Gebiet des anderen Vertragsstaates unverzüglich Leistungen erfordert und sich die Person nicht zum Zwecke der Inanspruchnahme einer ärztlichen Betreuung in den anderen Vertragsstaat begeben hat, oder c) sich auf Grund des Artikels 6 Absatz 2 und der Artikel 7 bis 9 des Abkommens im Gebiet des anderen Vertragsstaats aufhält und deren Zustand Leistungen erforderlich macht, oder d) die vom zuständigen Träger die Genehmigung erhält, sich in das Gebiet des anderen Vertragsstaates zu begeben, um dort eine ihrem Zustand angemessene Behandlung zu erhalten, hat Anspruch auf Sachleistungen zu Lasten des zuständigen Trägers vom Träger des Wohn- oder Aufenthaltsortes nach den für diesen Träger geltenden Rechtsvorschriften, als ob sie bei diesem versichert wäre. (2) Im Falle des Absatzes 1 dieses Artikels hängt die Gewährung von Körperersatzstücken, größeren Hilfsmitteln und anderen Sachleistungen von erheblicher Bedeutung davon ab, dass der zuständige Träger hiezu seine Zustimmung gibt, es sei denn, dass die Gewährung der Leistung nicht aufgeschoben werden kann, ohne das Leben oder die Gesundheit der betreffenden Person ernsthaft zu gefährden oder dass nach Artikel 15 Absatz 2 des Abkommens eine Kostenerstattung durch Pauschalzahlungen erfolgt. (3) Die Absätze 1 und 2 dieses Artikels sind auf Familienangehörige entsprechend anzuwenden. (4) Absatz 1 Buchstaben b, c und d dieses Artikels gelten in Bezug auf die Erbringung von Sachleistungen durch freiberuflich tätige Ärzte, Zahnärzte und Dentisten, sofern diese Leistungen für die in Artikel 14 des Abkommens bezeichneten Träger erbringen, nur hinsichtlich folgender Personen: a) Personen, die sich in Ausübung ihrer Beschäftigung im anderen Vertragsstaat aufhalten, sowie die sie begleitenden Familienangehörigen; b) Personen, die ihre im anderen Vertragsstaat wohnende Familie besuchen; c) Personen, die sich aus anderen Gründen im anderen Vertragsstaat aufhalten, wenn ihnen eine ambulante Behandlung für Rechnung des für ihren Aufenthaltsort in Betracht kommenden Trägers gewährt wurde.Artikel 12 Geldleistungen (1) In den Fällen des Artikels 11 des Abkommens sind die Geldleistungen vom zuständigen Träger nach den für ihn geltenden Rechtsvorschriften zu gewähren. (2) Hängt nach den Rechtsvorschriften eines Vertragsstaates die Höhe der Geldleistungen von der Zahl der Familienangehörigen ab, so berücksichtigt der zuständige Träger auch die Familienangehörigen, die im Gebiet des anderen Vertragsstaates wohnen.Artikel 13 Pensionsbezieher (1) Für eine Person, die nach den Rechtsvorschriften beider Vertragsstaaten zum Bezug einer Pension berechtigt ist, gelten ausschließlich die Rechtsvorschriften des Vertragsstaates, in dessen Gebiet sie wohnt. (2) Für eine Person, die nach den Rechtsvorschriften eines Vertragsstaates zum Bezug einer Pension berechtigt ist und im Gebiet des anderen Vertragsstaates wohnt, gelten die Rechtsvorschriften dieses Vertragsstaates, als ob sie zum Bezug einer Pension nach den Rechtsvorschriften dieses Vertragsstaates berechtigt wäre. (3) Die Absätze 1 und 2 dieses Artikels gelten entsprechend für Pensionswerber.Artikel 14 Träger des Aufenthalts- oder Wohnortes In den Fällen des Artikels 11 Absatz 1 des Abkommens und des Artikels 13 Absatz 2 des Abkommens werden die Leistungen gewährt a) in der Republik Österreich von der für den Aufenthalts- oder Wohnort der betreffenden Person in Betracht kommenden Gebietskrankenkasse, b) in der Republik Serbien von der für den Aufenthalts- oder Wohnort der betreffenden Person in Betracht kommenden Organisationseinheit des Krankenversicherungsträgers.Artikel 15 Kostenerstattung (1) Der zuständige Träger erstattet dem Träger, der die Sachleistungen gewährt hat, die nach Artikel 11 des Abkommens und Artikel 13 Absatz 2 des Abkommens aufgewendeten Beträge mit Ausnahme der Verwaltungskosten. (2) Die Verbindungsstellen der Vertragsstaaten können zur verwaltungsmäßigen Vereinfachung für alle Fälle oder für bestimmte Gruppen von Fällen andere Arten der Kostenerstattung vereinbaren.Kapitel 2 Arbeitsunfälle und Berufskrankheiten Artikel 16 Sachleistungen (1) Eine Person, die wegen eines Arbeitsunfalles oder einer Berufskrankheit Anspruch auf Sachleistungen nach den Rechtsvorschriften eines Vertragsstaates hat und die sich im Gebiet des anderen Vertragsstaates aufhält oder dort wohnt, hat Anspruch auf Sachleistungen zu Lasten des zuständigen Trägers vom Träger ihres Aufenthalts- oder Wohnortes nach den für diesen Träger geltenden Rechtsvorschriften, als ob sie bei diesem versichert wäre. Hinsichtlich der Gewährung von Körperersatzstücken, größeren Hilfsmitteln und anderen Sachleistungen von erheblicher Bedeutung gilt die Bestimmung des Artikels 11 Absatz 2 des Abkommens entsprechend. (2) In den Fällen des Absatzes 1 dieses Artikels werden die Sachleistungen gewährt a) in der Republik Österreich von der für den Aufenthalts- oder Wohnort der betreffenden Person in Betracht kommenden Gebietskrankenkasse, b) in der Republik Serbien von der für den Aufenthalts- oder Wohnort der betreffenden Person in Betracht kommenden Organisationseinheit des Krankenversicherungsträgers. (3) Anstelle des in Absatz 2 dieses Artikels genannten österreichischen Trägers kann ein Träger der Unfallversicherung die Sachleistungen erbringen. (4) Für die Erstattung der nach Absatz 1 dieses Artikels entstandenen Kosten gilt Artikel 15 des Abkommens entsprechend.Artikel 17 Berufskrankheiten (1) Hängt die Gewährung der Leistungen für eine Berufskrankheit nach den Rechtsvorschriften eines Vertragsstaates davon ab, dass die Krankheit zum ersten Mal im Gebiet dieses Vertragsstaates ärztlich festgestellt worden ist, so gilt diese Bedingung als erfüllt, wenn die betreffende Krankheit zum ersten Mal im Gebiet des anderen Vertragsstaates festgestellt worden ist. (2) Sehen die Rechtsvorschriften eines Vertragsstaates vor, dass Leistungen für eine Berufskrankheit nur gewährt werden, wenn die Tätigkeit, welche die Krankheit verursachen kann, während einer Mindestdauer ausgeübt wurde, so berücksichtigt der zuständige Träger dieses Vertragsstaates, soweit erforderlich, die Zeiten der Ausübung einer solchen Tätigkeit, während derer die Rechtsvorschriften des anderen Vertragsstaates galten.Artikel 18 Entschädigung von Berufskrankheiten (1) Wäre eine Berufskrankheit nach den Rechtsvorschriften beider Vertragsstaaten zu entschädigen, so sind Leistungen nur nach den Rechtsvorschriften des Vertragsstaates zu gewähren, in dessen Gebiet zuletzt eine Beschäftigung ausgeübt wurde, die geeignet ist, eine solche Berufskrankheit zu verursachen. (2) Bezog oder bezieht eine Person, die sich eine Berufskrankheit zugezogen hat, Leistungen zu Lasten eines Trägers eines Vertragsstaates und beansprucht sie, nachdem sie auch eine Tätigkeit nach den Rechtsvorschriften des anderen Vertragsstaates ausgeübt hat, welche eine Berufskrankheit verursachen kann, wegen Verschlimmerung Leistungen von einem Träger des anderen Vertragsstaates, so trägt der Träger des ersten Vertragsstaates weiterhin die Kosten der Leistungen ohne Berücksichtigung der Verschlimmerung nach den für ihn geltenden Rechtsvorschriften. Der zuständige Träger des zweiten Vertragsstaates gewährt eine Leistung in der Höhe des Unterschiedsbetrages zwischen der nach der Verschlimmerung geschuldeten Leistung und der Leistung, die vor der Verschlimmerung nach den für ihn geltenden Rechtsvorschriften geschuldet worden wäre, wenn die Krankheit nach diesen Rechtsvorschriften eingetreten wäre.Kapitel 3 Alter, Invalidität und Tod (Pensionen) Artikel 19 Zusammenrechnung der Versicherungszeiten (1) Hängt nach den Rechtsvorschriften eines Vertragsstaates der Erwerb, die Aufrechterhaltung oder das Wiederaufleben eines Leistungsanspruches von der Zurücklegung von Versicherungszeiten ab, so hat der zuständige Träger dieses Vertragsstaates, soweit erforderlich, die nach den Rechtsvorschriften des anderen Vertragsstaates zurückgelegten Versicherungszeiten zu berücksichtigen, als wären es nach den von ihm anzuwendenden Rechtsvorschriften zurückgelegte Versicherungszeiten, soweit sie nicht auf dieselbe Zeit entfallen. (2) Hängt nach den Rechtsvorschriften eines Vertragsstaates die Gewährung bestimmter Leistungen von der Zurücklegung der Versicherungszeiten in einem Beruf, für den ein Sondersystem besteht, oder in einem bestimmten Beruf oder in einer bestimmten Beschäftigung ab, so sind für die Gewährung dieser Leistungen die nach den Rechtsvorschriften des anderen Vertragsstaates zurückgelegten Versicherungszeiten nur zu berücksichtigen, wenn sie in einem entsprechenden System oder, wenn ein solches nicht besteht, im gleichen Beruf oder in der gleichen Beschäftigung zurückgelegt worden sind. (3) Verlängern nach den Rechtsvorschriften eines Vertragsstaates Zeiten der Pensionsgewährung den Zeitraum, in dem die Versicherungszeiten zurückgelegt sein müssen, so verlängert sich dieser Zeitraum auch durch entsprechende Zeiten der Pensionsgewährung nach den Rechtsvorschriften des anderen Vertragsstaates. (4) Die in Tagen ausgedrückten serbischen Versicherungszeiten hat der österreichische Träger in Monate umzurechnen, wobei 30 Tage einem Monat entsprechen; Resttage gelten hiebei als ein ganzer Monat.Artikel 20 Versicherungszeiten unter einem Jahr (1) Erreichen die Versicherungszeiten, die nach den Rechtsvorschriften eines Vertragsstaates zu berücksichtigen sind, insgesamt nicht zwölf Monate für die Berechnung der Leistung, so wird nach diesen Rechtsvorschriften keine Leistung gewährt. Dies gilt nicht, wenn nach diesen Rechtsvorschriften ein Leistungsanspruch allein auf Grund dieser Versicherungszeiten besteht. (2) Die in Absatz 1 erster Satz dieses Artikels genannten Versicherungszeiten sind von dem Träger des anderen Vertragsstaates für den Erwerb, die Aufrechterhaltung und das Wiederaufleben eines Leistungsanspruches sowie dessen Ausmaß so zu berücksichtigen, als wären es nach den von ihm anzuwendenden Rechtsvorschriften zurückgelegte Versicherungszeiten.Artikel 21 Feststellung der Leistungen (1) Besteht nach den Rechtsvorschriften eines Vertragsstaates auch ohne Anwendung des Artikels 19 des Abkommens ein Leistungsanspruch, so hat der zuständige Träger dieses Vertragsstaates die Leistung ausschließlich auf Grund der nach diesen Rechtsvorschriften zu berücksichtigenden Versicherungszeiten festzustellen. (2) Besteht nach den Rechtsvorschriften eines Vertragsstaates nur unter Anwendung des Artikels 19 des Abkommens ein Leistungsanspruch, so hat der zuständige Träger dieses Vertragsstaates die Leistung nach Artikel 22 des Abkommens beziehungsweise Artikel 23 des Abkommens festzustellen.Artikel 22 Berechnung von österreichischen Teilleistungen (1) Im Falle des Artikels 21 Absatz 2 des Abkommens hat der zuständige österreichische Träger die Leistung unter Anwendung der Verordnung (EG) Nr. 883/2004 zu berechnen, wobei serbische Versicherungszeiten als Versicherungszeiten eines anderen Mitgliedstaates der Europäischen Union gelten. (2) Abweichend von Absatz 1 dieses Artikels sind Kindererziehungszeiten ausschließlich nach Maßgabe der österreichischen Rechtsvorschriften bei der Leistungsberechnung zu berücksichtigen.Artikel 23 Berechnung von serbischen Teilleistungen Im Falle des Artikels 21 Absatz 2 des Abkommens hat der zuständige serbische Träger die Leistung auf die Weise festzustellen, dass er zunächst den Leistungsbetrag berechnet, der zustehen würde, wenn die gesamten für die Berechnung der Leistung zu berücksichtigenden Versicherungszeiten nach den serbischen Rechtsvorschriften zurückgelegt wären, jedoch höchstens bis zur vollen Höhe der Leistung, und sodann auf Grund des so berechneten Betrages den Leistungsbetrag im Verhältnis zwischen den ausschließlich nach den serbischen Rechtsvorschriften zu berücksichtigenden Versicherungszeiten und den gesamten für die Berechnung der Leistung berücksichtigten Versicherungszeiten feststellt.Kapitel 4 Arbeitslosigkeit Artikel 24 Zusammenrechnung der Versicherungszeiten (1) Nach den Rechtsvorschriften eines Vertragsstaates zurückgelegte Versicherungszeiten sind für den Anspruch auf Leistungen bei Arbeitslosigkeit nach den Rechtsvorschriften des anderen Vertragsstaates zu berücksichtigen, sofern der Arbeitslose in dem Vertragsstaat, in dem er den Anspruch auf eine solche Leistung geltend macht, in den letzten zwölf Monaten vor Beantragung dieser Leistung insgesamt mindestens 26 Wochen ohne Verletzung der Vorschriften über die Beschäftigung von Ausländern als Arbeitnehmer versichert war. (2) Die Voraussetzung der Mindestbeschäftigungszeit von 26 Wochen gilt nicht für Arbeitslose, deren Beschäftigung für längere Zeit in Aussicht genommen war, jedoch früher als nach 26 Wochen ohne ihr Verschulden geendet hat, oder welche die Staatsangehörigkeit des Vertragsstaates besitzen, in dem sie die Leistung beantragen. In Bezug auf Österreich stehen den österreichischen Staatsangehörigen die Staatsangehörigen anderer Staaten, für die der europarechtliche Grundsatz der Freizügigkeit gilt, gleich.Artikel 25 Bezugsdauer Die Bezugsdauer wird um die Zeit gemindert, in der der Arbeitslose im anderen Vertragsstaat innerhalb der letzten zwölf Monate vor dem Tag der Antragstellung bereits eine Leistung bei Arbeitslosigkeit bezogen hat.ABSCHNITT IV VERSCHIEDENE BESTIMMUNGEN Artikel 26 Aufgaben der zuständigen Behörden, Rechts- und Amtshilfe (1) Die zuständigen Behörden werden die zur Durchführung dieses Abkommens notwendigen Verwaltungsmaßnahmen in einer Vereinbarung regeln. (2) Die zuständigen Behörden der beiden Vertragsstaaten unterrichten einander a) über alle zur Anwendung dieses Abkommens getroffenen Maßnahmen; b) über alle die Anwendung dieses Abkommens berührenden Änderungen ihrer Rechtsvorschriften. (3) Bei der Anwendung dieses Abkommens haben die Behörden und Träger der Vertragsstaaten einander zu unterstützen und wie bei der Anwendung ihrer eigenen Rechtsvorschriften zu handeln. Diese Amtshilfe ist kostenlos. (4) Die Träger und Behörden der Vertragsstaaten können zwecks Anwendung dieses Abkommens miteinander sowie mit beteiligten Personen oder deren Beauftragten unmittelbar in Verbindung treten. (5) Die Träger und Behörden eines Vertragsstaates dürfen die bei ihnen eingereichten Anträge und sonstige Schriftstücke nicht deshalb zurückweisen, weil sie in der Amtssprache des anderen Vertragsstaates abgefasst sind. (6) Ärztliche Untersuchungen, die in Durchführung der Rechtsvorschriften nur eines Vertragsstaates vorgenommen werden und Personen betreffen, die sich im Gebiet des anderen Vertragsstaates aufhalten oder dort wohnen, werden auf Ersuchen des zuständigen Trägers zu seinen Lasten vom Träger des Aufenthalts- oder Wohnortes veranlasst. Werden ärztliche Untersuchungen in Durchführung der Rechtsvorschriften der beiden Vertragsstaaten durchgeführt, werden sie vom Träger des Aufenthalts- oder Wohnortes der betreffenden Person zu seinen Lasten veranlasst. (7) Für die gerichtliche Rechtshilfe gelten die jeweiligen auf die Rechtshilfe in bürgerlichen Rechtssachen anwendbaren Bestimmungen.Artikel 27 Verbindungsstellen Die zuständigen Behörden haben in der Vereinbarung nach Artikel 26 Absatz 1 des Abkommens zur Erleichterung der Durchführung dieses Abkommens, insbesondere zur Herstellung einer einfachen und raschen Verbindung zwischen den beiderseits in Betracht kommenden Trägern, Verbindungsstellen zu errichten.Artikel 28 Befreiung von Steuern und Beglaubigungen (1) Jede in den Vorschriften eines Vertragsstaates vorgesehene Befreiung oder Ermäßigung von Steuern oder Gebühren für Schriftstücke oder Urkunden, die in Anwendung dieser Rechtsvorschriften vorzulegen sind, wird auf die entsprechenden Schriftstücke und Urkunden erstreckt, die in Anwendung dieses Abkommens oder der Rechtsvorschriften des anderen Vertragsstaates vorzulegen sind. (2) Urkunden, Dokumente und Schriftstücke jeglicher Art, die in Anwendung dieses Abkommens vorgelegt werden müssen, bedürfen keiner Beglaubigung.Artikel 29 Einreichung von Schriftstücken (1) Anträge, Erklärungen oder Rechtsmittel, die in Anwendung dieses Abkommens oder der Rechtsvorschriften eines Vertragsstaates bei einer Behörde, einem Träger oder einer sonstigen zuständigen Einrichtung eines Vertragsstaates eingereicht werden, sind als bei einer Behörde, einem Träger oder einer sonstigen zuständigen Einrichtung des anderen Vertragsstaates eingereichte Anträge, Erklärungen oder Rechtsmittel anzusehen. (2) Ein nach den Rechtsvorschriften des einen Vertragsstaates gestellter Antrag auf eine Leistung gilt auch als Antrag auf eine entsprechende Leistung nach den Rechtsvorschriften des anderen Vertragsstaates, die unter Berücksichtigung dieses Abkommens in Betracht kommt; dies gilt nicht, wenn der Antragsteller ausdrücklich beantragt, dass die Feststellung einer nach den Rechtsvorschriften eines Vertragsstaates erworbenen Leistung bei Alter aufgeschoben wird. (3) Anträge, Erklärungen oder Rechtsmittel, die in Anwendung der Rechtsvorschriften eines Vertragsstaates innerhalb einer bestimmten Frist bei einer Behörde, einem Träger oder einer sonstigen zuständigen Einrichtung dieses Vertragsstaates einzureichen sind, können innerhalb der gleichen Frist bei der entsprechenden Stelle des anderen Vertragsstaates eingereicht werden. (4) In den Fällen der Absätze 1 bis 3 dieses Artikels übermittelt die in Anspruch genommene Stelle diese Anträge, Erklärungen oder Rechtsmittel entweder unmittelbar oder durch Vermittlung der Verbindungsstellen der Vertragsstaaten unverzüglich an die entsprechende zuständige Stelle des anderen Vertragsstaates.Artikel 30 Zahlungsverkehr (1) Die nach diesem Abkommen leistungspflichtigen Träger können Leistungen an Berechtigte im anderen Vertragsstaat mit befreiender Wirkung in der für sie innerstaatlich maßgebenden Währung oder in einer anderen konvertierbaren Währung erbringen. (2) Die in diesem Abkommen vorgesehenen Erstattungen haben in der Währung des Vertragsstaates zu erfolgen, in dem der Träger, der die Leistungen gewährt hat, seinen Sitz hat.Artikel 31 Vollstreckungsverfahren (1) Die vollstreckbaren Entscheidungen der Gerichte sowie die vollstreckbaren Bescheide und Rückstandsausweise (Urkunden) der Träger oder der Behörden eines Vertragsstaates über Beiträge und sonstige Forderungen aus der sozialen Sicherheit werden im anderen Vertragsstaat anerkannt. (2) Die Anerkennung darf nur versagt werden, wenn sie der öffentlichen Ordnung des Vertragsstaates widerspricht, in dem die Entscheidung oder die Urkunde anerkannt werden soll. (3) Die nach Absatz 1 dieses Artikels anerkannten vollstreckbaren Entscheidungen und Urkunden werden im anderen Vertragsstaat vollstreckt. Das Vollstreckungsverfahren richtet sich nach den Rechtsvorschriften, die in dem Vertragsstaat, in dessen Gebiet vollstreckt werden soll, für die Vollstreckung der in diesem Vertragsstaat erlassenen entsprechenden Entscheidungen und Urkunden gelten. Die Ausfertigung der Entscheidung oder der Urkunde muss mit der Bestätigung ihrer Vollstreckbarkeit (Vollstreckungsklausel) versehen sein.Artikel 32 Verrechnung von Vorschüssen und Rückforderung nicht geschuldeter Leistungen (1) Hat ein Träger eines Vertragsstaates einen Vorschuss gezahlt, so kann die auf denselben Zeitraum entfallende Nachzahlung einer entsprechenden Leistung, auf die nach den Rechtsvorschriften des anderen Vertragsstaates Anspruch besteht, einbehalten werden. (2) Hat ein Träger des einen Vertragsstaates für eine Zeit, für die ein Träger des anderen Vertragsstaates nachträglich eine entsprechende Leistung zu erbringen hat, eine höhere als die gebührende Leistung gezahlt, so gilt der diese Leistung übersteigende Betrag bis zur Höhe des nachzuzahlenden Betrages als Vorschuss im Sinne des Absatzes 1 dieses Artikels. (3) Hat ein Träger der Sozialhilfe eines Vertragsstaates eine Leistung der Sozialhilfe während eines Zeitraumes gewährt, für den nachträglich nach den Rechtsvorschriften des anderen Vertragsstaates Anspruch auf Geldleistungen entsteht, so behält der zuständige Träger dieses Vertragsstaates auf Ersuchen und für Rechnung des Trägers der Sozialhilfe die auf den gleichen Zeitraum entfallenden Nachzahlungen bis zur Höhe der gezahlten Leistungen der Sozialhilfe ein, als ob es sich um eine vom Träger der Sozialhilfe des letzteren Vertragsstaates gezahlte Leistung der Sozialhilfe handeln würde.Artikel 33 Schadenersatz (1) Hat eine Person, die nach den Rechtsvorschriften eines Vertragsstaates Leistungen für einen Schaden zu erhalten hat, der im Gebiet des anderen Vertragsstaates eingetreten ist, nach dessen Vorschriften gegen einen Dritten Anspruch auf Ersatz des Schadens, so geht der Ersatzanspruch auf den Träger des ersten Vertragsstaates nach den für ihn geltenden Rechtsvorschriften über. (2) Stehen Ersatzansprüche hinsichtlich gleichartiger Leistungen aus demselben Schadensfall sowohl einem Träger des einen Vertragsstaates als auch einem Träger des anderen Vertragsstaates zu, so kann der Dritte die nach Absatz 1 dieses Artikels auf die beiden Träger übergegangenen Ansprüche mit befreiender Wirkung durch Zahlung an den einen oder anderen Träger befriedigen. Im Innenverhältnis sind die Träger anteilig im Verhältnis der von Ihnen zu erbringenden Leistungen ausgleichspflichtig.Artikel 34 Schutz der personenbezogenen Daten Für die nach diesem Abkommen ausgetauschten personenbezogenen Daten gelten die nachfolgenden Bestimmungen unter Einhaltung der Rechtsvorschriften der Vertragsstaaten. a) Die Daten dürfen für die Durchführung dieses Abkommens und der Rechtsvorschriften, auf die es sich bezieht, an die danach im Empfängerstaat zuständigen Stellen übermittelt werden. Die zuständige empfangende Stelle darf sie nur für diese Zwecke verwenden. Eine Weiterübermittlung im Empfängerstaat an andere Stellen ist nur im Rahmen des innerstaatlichen Rechts des Empfängerstaates zulässig, wenn dies Zwecken der sozialen Sicherheit einschließlich damit zusammenhängender gerichtlicher Verfahren dient. Auch im Falle einer Offenlegung von Informationen im Rahmen eines Gerichtsverfahrens oder einer Gerichtsentscheidung darf die Vertraulichkeit personenbezogener Daten nur durchbrochen werden, soweit dies zur Wahrung überwiegender berechtigter Interessen einer anderen Person oder überwiegender öffentlicher Interessen notwendig ist. b) Werden personenbezogene Daten auf Grund dieses Abkommens oder der Vereinbarung zu seiner Durchführung zwischen den zuständigen Behörden, Trägern und sonstigen in Betracht kommenden Stellen der beiden Vertragsstaaten in welcher Form auch immer übermittelt, so sind sie ebenso geheim zu halten wie die auf Grund des innerstaatlichen Rechts dieses Staates beschafften Informationen gleicher Art. Diese Verpflichtungen gelten für alle mit der Erfüllung von Aufgaben im Sinne dieses Abkommens betrauten Personen und auch gegenüber solchen, die selbst zur Verschwiegenheit verpflichtet sind. c) Die empfangende Stelle unterrichtet die übermittelnde Stelle auf Ersuchen in Einzelfällen über die Verwendung der übermittelten Daten und über die dadurch erzielten Ergebnisse. d) Die übermittelnde Stelle ist verpflichtet, auf die Richtigkeit der zu übermittelnden Daten sowie auf die Erforderlichkeit und Verhältnismäßigkeit in Bezug auf den mit der Übermittlung verfolgten Zweck zu achten. Erweist sich, dass unrichtige Daten oder Daten, die nach dem innerstaatlichen Recht des übermittelnden Vertragstaates nicht hätten übermittelt werden dürfen, übermittelt worden sind, so ist dies der empfangenden Stelle unverzüglich mitzuteilen. Sie ist verpflichtet, umgehend die Berichtigung oder Löschung vorzunehmen. Hat die empfangende Stelle Grund zur Annahme, dass übermittelte Daten unrichtig sind oder zu löschen wären, so unterrichtet sie die übermittelnde Stelle unverzüglich hierüber. e) Dem Betroffenen, der seine Identität in geeigneter Form nachweist, ist auf seinen Antrag von der für die Verarbeitung verantwortlichen Stelle ohne Verzögerung in allgemein verständlicher Form über die zu seiner Person übermittelten beziehungsweise verarbeiteten Informationen, deren Herkunft, allfällige Empfänger oder Empfängerkategorien, den vorgesehenen Verwendungszweck, sowie die Rechtsgrundlage der Übermittlung beziehungsweise Verarbeitung Auskunft zu erteilen. Darüber hinaus hat der Betroffene das Recht auf Richtigstellung unrichtiger Daten und Löschung unzulässigerweise verwendeter Daten. f) Die Vertragsstaaten stellen sicher, dass der Betroffene sich im Falle der Verletzung seiner Datenschutzrechte mit einer wirksamen Beschwerde an ein Gericht oder eine andere unabhängige Behörde wenden kann und dass ihm ein Recht auf die Geltendmachung eines Schadenersatzanspruch zusteht. g) Die Vertragsstaaten haften nach Maßgabe ihres innerstaatlichen Rechts für Schäden, die ein Betroffener als Folge der Verarbeitung von nach diesem Abkommen übermittelten personenbezogenen Daten erlitten hat. h) Hat eine Stelle des einen Vertragsstaates personenbezogene Daten auf Grund dieses Abkommens übermittelt, kann die empfangende Stelle des anderen Vertragsstaates sich im Rahmen ihrer Haftung nach Maßgabe des innerstaatlichen Rechts gegenüber dem Geschädigten zu ihrer Entlastung nicht darauf berufen, dass die übermittelten Daten unrichtig gewesen sind. Leistet die empfangende Stelle Schadenersatz wegen eines Schadens, der durch die Verwendung von unrichtig übermittelten Daten verursacht wurde, so erstattet die übermittelnde Stelle der empfangenden Stelle auf deren Ersuchen den Gesamtbetrag des geleisteten Ersatzes. i) Übermittelte personenbezogene Daten sind zu löschen, wenn sich deren Unrichtigkeit ergibt, deren Beschaffung oder Übermittlung nicht rechtmäßig erfolgte, rechtmäßig übermittelte Daten gemäß dem Recht des übermittelnden Staates zu einem späteren Zeitpunkt zu löschen sind oder sie zu dem Zweck, zu dem sie übermittelt worden sind, nicht mehr erforderlich sind und kein Grund zu der Annahme besteht, dass durch die Löschung schutzwürdige Interessen des Betroffenen im Bereich der sozialen Sicherheit beeinträchtigt werden. j) Die übermittelnde und die empfangende Stelle sind verpflichtet, Anlass, Inhalt, Zeitpunkt und Empfangsstelle beziehungsweise Sendestelle in Bezug auf die Übermittlung beziehungsweise den Empfang personenbezogener Daten festzuhalten. Die Protokolldaten sind mindestens drei Jahre aufzubewahren und dürfen ausschließlich zur Kontrolle der Einhaltung der Datenschutzvorschriften verwendet werden. k) Die übermittelnde und die empfangende Stelle sind verpflichtet, personenbezogene Daten, die übermittelt werden, wirksam gegen zufällige oder unbefugte Zerstörung, zufälligen Verlust, unbefugten Zugang, unbefugte oder zufällige Veränderung und unbefugte Bekanntgabe zu schützen. l) Alle personenbezogene Daten, die im Rahmen dieses Abkommens von einem Vertragsstaat dem anderen Vertragsstaat übermittelt werden, sollen vertraulich behandelt und nur für die Durchführung dieses Abkommens angewendet werden, es sei denn, dass ihre Offenlegung auf Grund des innerstaatlichen Rechts eines der Vertragsstaaten erforderlich ist.Artikel 35 Streitbeilegung Streitigkeiten zwischen den Vertragsstaaten über die Auslegung oder Anwendung dieses Abkommens sollen durch die zuständigen Behörden der Vertragsstaaten beigelegt werden.ABSCHNITT V ÜBERGANGS- UND SCHLUSSBESTIMMUNGEN Artikel 36 Übergangsbestimmungen (1) Dieses Abkommen begründet keinen Anspruch auf Leistungen für die Zeit vor seinem Inkrafttreten. (2) Für die Feststellung des Anspruches auf Leistungen nach diesem Abkommen werden auch Versicherungszeiten berücksichtigt, die nach den Rechtsvorschriften eines Vertragsstaates vor Inkrafttreten dieses Abkommens zurückgelegt worden sind. (3) Vorbehaltlich des Absatzes 1 dieses Artikels gilt dieses Abkommen auch für Versicherungsfälle, die vor seinem Inkrafttreten eingetreten sind, soweit nicht früher festgestellte Ansprüche durch Kapitalzahlungen abgegolten worden sind. (4) Leistungen, die vor dem Inkrafttreten dieses Abkommens festgestellt wurden, sind nicht neu festzustellen. (5) Für Dienstnehmer, die vom Wahlrecht nach Artikel 8 Absatz 2 des am 5. Juni 1998 unterzeichnete Abkommens zwischen der Republik Österreich und der Bundesrepublik Jugoslawien über soziale Sicherheit vom Gebrauch gemacht haben, gelten weiterhin die Rechtsvorschriften des Vertragsstaates, die bis zum Inkrafttreten dieses Abkommens für den Dienstnehmer gegolten haben.Artikel 37 Versicherungslastregelungen (1) Die Träger in der Republik Österreich übernehmen alle Anwartschaften und Ansprüche von Personen, die am 1. Jänner 1956 österreichische Staatsangehörige waren oder die nach den österreichischen Rechtsvorschriften für den Bereich der Sozialversicherung als Volksdeutsche anerkannt werden, vorausgesetzt, dass sich die betreffenden Personen am 1. Jänner 1956 im Gebiet der Republik Österreich nicht nur vorübergehend aufgehalten haben, soweit diese Anwartschaften und Ansprüche auf Grund der vor dem 1. Jänner 1956 in der ehemaligen jugoslawischen Sozialversicherung (Pensions- oder Invaliditätsversicherung) zurückgelegten Versicherungszeiten entstanden sind. (2) Die Träger in der Republik Serbien übernehmen alle Anwartschaften und Ansprüche von serbischen Staatsangehörigen, die als jugoslawische Staatsangehörige am 1. Jänner 1956 ihren Wohnort im Gebiet des ehemaligen Jugoslawien hatten, soweit diese Anwartschaften und Ansprüche auf Grund der vor dem 1. Jänner 1956 in der österreichischen Sozialversicherung (Pensions- oder Unfallversicherung) zurückgelegten Versicherungszeiten entstanden sind. (3) Sind Leistungsansprüche zu übernehmen, so gelten die allgemeinen Voraussetzungen für die entsprechenden Leistungen als erfüllt. Hiebei sind für die Bemessung der Leistungen die den übernommenen Leistungsansprüchen zugrunde liegenden Versicherungszeiten heranzuziehen. Erreichen diese Zeiten nicht das Ausmaß der für die entsprechenden Pensionen erforderlichen Wartezeit, so sind der Leistungsbemessung Zeiten im Ausmaß dieser Wartezeit zugrunde zu legen. (4) Für die Berücksichtigung der Anwartschaften und Ansprüche in Leistungen der österreichischen Pensions- oder Unfallversicherung sind die Rechtsvorschriften des österreichischen Bundesgesetzes vom 22. November 1961 über Leistungsansprüche und Anwartschaften in der Pensions-(Renten-)Versicherung und Unfallversicherung auf Grund von Beschäftigungen im Ausland anzuwenden.Artikel 38 Inkrafttreten (1) Dieses Abkommen ist zu ratifizieren. Die Ratifikationsurkunden sind so bald wie möglich in Wien auszutauschen. (2) Dieses Abkommen tritt am ersten Tag des zweitfolgenden Monats nach Ablauf des Monats in Kraft, in dem die Ratifikationsurkunden ausgetauscht werden.Artikel 39 Außerkrafttreten Mit Inkrafttreten dieses Abkommens tritt im Verhältnis zwischen der Republik Österreich und der Republik Serbien das am 5. Juni 19981 unterzeichnete Abkommen zwischen der Republik Österreich und der Bundesrepublik Jugoslawien über soziale Sicherheit außer Kraft. _________________________________ 1 Kundgemacht in BGBl. III Nr. 100/2002.Artikel 40 Geltungsdauer und Kündigung (1) Dieses Abkommen wird auf unbestimmte Zeit geschlossen. Jeder Vertragsstaat kann es unter Einhaltung einer Frist von drei Monaten zum Ende eines Kalenderjahres schriftlich auf diplomatischem Weg kündigen. (2) Im Falle der Kündigung dieses Abkommens gelten die Bestimmungen dieses Abkommens für erworbene Ansprüche weiter. ZU URKUND DESSEN haben die Bevollmächtigten dieses Abkommen unterzeichnet und mit Siegeln versehen. GESCHEHEN zu Belgrad, am 26. Jänner 2012 in zwei Urschriften in deutscher und serbischer Sprache, wobei beide Texte in gleicher Weise authentisch sind.",  "parts": [    {      "@id": "6916ac50-6e70-4e53-b489-6f74b7814847#offset_660_2607",      "offset_ini": 660,      "offset_end": 2607,      "title": {"de":"Begriffsbestimmungen"}    }]  }                    